import React, { useState } from "react";

function CreateArea(props) {
    const [cont,changeCont] = useState({
        title:"",
        content:""
    });
    function updateText(event){
        var {name,value} = event.target;
        changeCont((prevState)=>{
            return{
                ...prevState,
                [name]: value
            }
        })
    }

  return (
    <div>
      <form>
        <input name="title" placeholder="Title"  onChange={updateText}/>
        <textarea name="content" placeholder="Take a note..." rows="3"  onChange={updateText}/>  
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
